import axios from 'axios'
import React, { Component } from 'react'
import app from '../../firebase.js'

class App extends Component {
  state = {
    // Initially, no file is selected
    selectedFile: null,
    ulink: null,
  }

  // On file select (from the pop up)
  onFileChange = (event) => {
    // Update the state
    this.setState({ selectedFile: event.target.files[0] })
  }

  // On file upload (click the upload button)
  onFileUpload = () => {
    // Create an object of formData
    const formData = new FormData()

    // Update the formData object
    formData.append(
      'myFile',
      this.state.selectedFile,
      this.state.selectedFile.name
    )

    // Details of the uploaded file
    console.log(this.state.selectedFile)

    // Request made to the backend api
    // Send formData object
    axios.post('api/uploadfile', formData)

    // Get File
    let file = document.getElementById('image').files[0]

    // Create a storage ref

    let storageRef = app.storage().ref('images/' + file.name)

    //Upload file

    let task = storageRef.put(file)

    // get the URL
    task
      .then((snapshot) => snapshot.ref.getDownloadURL())
      .then((url) =>
        this.setState(
          { ulink: url },
          // passing back the url to the parent
          this.props.setURL(url),
          axios.post('http://localhost:8080/dashboard', {
            ulink: 'fasdfasfa',
          })
        )
      )
  }

  // File content to be displayed after
  // file upload is complete
  fileData = () => {
    if (this.state.selectedFile) {
      return (
        <div>
          <h2>File Details:</h2>
          <p>File Name: {this.state.selectedFile.name}</p>
          <p>File Type: {this.state.selectedFile.type}</p>
          <p>
            Last Modified:{' '}
            {this.state.selectedFile.lastModifiedDate.toDateString()}
          </p>
        </div>
      )
    } else {
      return (
        <div>
          <br />
          <h4>Choose before Pressing the Upload button</h4>
        </div>
      )
    }
  }
  // upload image if url is existent
  fileImage = () => {
    // rendering picture if it exists
    if (this.state.ulink) {
      return <img src={this.state.ulink} alt='upload' />
    }
  }

  render() {
    console.log(this.state.ulink)
    if (!this.props.isVisible) {
      return null
    } else {
      return (
        <div>
          <div>
            <input type='file' id='image' onChange={this.onFileChange} />
            <button onClick={this.onFileUpload}>Upload!</button>
          </div>
          {this.fileData()}
          {this.fileImage()}
        </div>
      )
    }
  }
}

export default App
