import axios from 'axios'
import React, { Component } from 'react'

class Goal extends Component {
  state = {
    // Initially, no file is selected
    startDate: '',
    endDate: '',
    total: '',
    updateBudget: false,
  }

  componentDidMount() {
    axios.get('http://localhost:8080/goal').then((res) => {
      this.setState({
        startDate: res.data.startDate,
        endDate: res.data.endDate,
        total: res.data.goal,
      })
    })
  }

  // getting the date from the user
  onDateChange = (event) => {
    //update the state
    this.setState({ startDate: event.target.value })
  }

  // getting the firstWord from the user
  onExpenseChange = (event) => {
    //update the state
    this.setState({ expense: event.target.value })
  }
  // getting the lastWord from the user
  onEndDate = (event) => {
    //update the state
    this.setState({ endDate: event.target.value })
  }

  axiosPost = (event) => {
    event.preventDefault()
    this.setState({ updateBudget: false })
    axios.post('http://localhost:8080/goal', this.state)
  }
  showUpdate = () => {
    this.setState({ updateBudget: true })
  }

  render() {
    console.log(this.state)
    if (!this.props.isVisible) {
      return null
    } else if (this.state.total === '' || this.state.total === undefined) {
      return (
        <div>
          <h1>Lets set a budget!</h1>
          <form action=''>
            <label htmlFor='date'>Start Date</label>
            <input
              type='text'
              name='date'
              id='date'
              placeholder='mm/dd/yy'
              onChange={this.onDateChange}
              value={this.state.startDate}
            />
            <label htmlFor='date'>End Date</label>
            <input
              type='text'
              name='date'
              id='date'
              placeholder='mm/dd/yy'
              onChange={this.onEndDate}
              value={this.state.endDate}
            />
            <label htmlFor='expense'>Amount</label>
            <input
              type='text'
              name='expense'
              id='expense'
              placeholder='budget amount'
              onChange={this.onExpenseChange}
              value={this.state.total}
            />
            <button onClick={this.axiosPost}>Submit</button>
          </form>
        </div>
      )
    } else if (this.state.updateBudget) {
      return (
        <div>
          <h1>Lets set a budget!</h1>
          <form action=''>
            <label htmlFor='date'>Start Date</label>
            <input
              type='text'
              name='date'
              id='date'
              placeholder={this.state.startDate}
              onChange={this.onDateChange}
              value={this.state.startDate}
            />
            <label htmlFor='date'>End Date</label>
            <input
              type='text'
              name='date'
              id='date'
              placeholder={this.state.endDate}
              onChange={this.onEndDate}
              value={this.state.endDate}
            />
            <label htmlFor='expense'>Amount</label>
            <input
              type='text'
              name='expense'
              id='expense'
              placeholder={this.state.total}
              onChange={this.onExpenseChange}
              value={this.state.total}
            />
            <button onClick={this.axiosPost}>Submit</button>
          </form>
        </div>
      )
    } else {
      return (
        <div>
          <p>
            You have budgeted {this.state.total} for your budget from{' '}
            {this.state.startDate} to {this.state.endDate}{' '}
          </p>
          <button onClick={this.showUpdate}>Update Budget</button>
          <p></p>
        </div>
      )
    }
  }
}

export default Goal
